var resp = {};

var responseFormat = function() {
	return {
		message: '',
		response: null,
		status: 200
	};
};

var error = function(type, data) {
	return {
		type: (type) ? type: 'ERROR',
		data: (data) ? data: {},
	};
};

resp.error = function(message, data, status, type) {
	var r = responseFormat();
	r.message = (message) ? message: 'ERROR RESPONSE';
	r.status = (status) ? status: 400;
	r.error = error(type, data);
	return r;
};

resp.ok = function(message, response, status) {
	var r = responseFormat();
	r.message = (message) ? message: 'SUCCESS RESPONSE';
	r.status = (status) ? status: 200;
	r.response = (response) ? response: {};
	return r;
};

resp.response = function(message, response, status) {
	return resp.ok(message, response, status);
};

module.exports = resp;